package datatypes;

public class arthamaticOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int a=10;
		   int b=20;
		  int add = a+b;
		   System.out.println(add);

		   int f= 10;
		   double g= 2.5;
	        double h=f-g;
		   System.out.println(h);
		   
		   int aa=5;
			int bb=4;
			int multi=aa*bb;
			System.out.println("[" + aa + " * " + bb + " = " + multi +" ] ");
			
			int cc= 8;
			int dd= 2;
			int quo=cc/dd;
			System.out.println("{"+ cc + " / " + dd + " = " + quo + " } ");
	         int rem=cc%dd;
			System.out.println("{"+ cc + " % " + dd + " = " + rem + " } ");

	}

}
